mapper.insert:
INSERT INTO houseinfos (
    apt_seq, sgg_cd, umd_cd, umd_nm, jibun,
    road_nm_sgg_cd, road_nm, road_nm_bonbun, road_nm_bubun,
    apt_nm, build_year, latitude, longitude
)
VALUES (
    #{apt_seq}, #{sgg_cd}, #{umd_cd}, #{umd_nm}, #{jibun},
    #{road_nm_sgg_cd}, #{road_nm}, #{road_nm_bonbun}, #{road_nm_bubun},
    #{apt_nm}, #{build_year}, #{latitude}, #{longitude}
)

mapper.select:
SELECT apt_seq FROM houseinfos
AND apt_nm IN
#{name}
